import React, { Component } from "react";
import images from "./images.json";
import "./App.css";
import Jumbotron from "./components/jumbotron/index";
import ScoreHeader from "./components/scoreHeader/index";
import MemoryCard from "./components/memoryCard/index";
import Container from "./components/cardContainer/index";

class App extends Component {
  state = {
    images,
    score: 0
  };

  cardClick = (e) => {
    let keyList = [2, 3];
    const key = e.target.key;
    let score = this.state.score;
    if (!keyList.includes(key)) {
      keyList.push(key);
      score++;
      this.setState({
        score: score
      });
    } else {
      score = 0
      this.setState ({
        score: score
      });
      alert("you lost")
    } 
  }

  render() {
    return (
      <Container>
        <Jumbotron />
        <ScoreHeader score={this.state.score} />
        {this.state.images.map(image => (
          <MemoryCard key={image.id} name={image.name} image={image.image} onClick={this.cardClick}/>
        ))}
      </Container>
    );
  }
}

export default App;
