import React, { Component } from "react";
import images from "./images.json";
import "./App.css";
import Jumbotron from "./components/jumbotron/index";
import ScoreHeader from "./components/scoreHeader/index";
import MemoryCard from "./components/memoryCard/index";
import Container from "./components/cardContainer/index";
import shuffle from 'shuffle-array';
let keyList = [];

class App extends Component {
  state = {
    images,
    score: 0
  };

  cardShuffle = () => {
    shuffle(this.state.images);
  }

  cardShuffle;

  cardClick = (id) => {
    const key = this.state.images[id].id;
    let score = this.state.score;
    console.log("key: " + JSON.stringify(key));

    if (!keyList.includes(key)) {

      score++;
      this.setState({
        score: score
      });
      keyList.push(key);
      console.log("keyList: " + keyList)

    } else {
      score = 0
      keyList = [];
      this.setState ({
        score: score
      });
      alert("you lost")
    } 
  }

  render() {
    return (
      <Container>
        <Jumbotron />
        <ScoreHeader score={this.state.score} />
        {this.state.images.map(image => (
          <MemoryCard 
          shuffle={this.cardShuffle}
          cardClick={this.cardClick} 
          key={image.id} 
          id={image.id}
          name={image.name} 
          image={image.image} 
          />
        ))}
      </Container>
    );
  }
}

export default App;
