import React, { Component } from "react";
import images from "./images.json";
import "./App.css";
import Jumbotron from "./components/jumbotron/index";
import ScoreHeader from "./components/scoreHeader/index";
import MemoryCard from "./components/memoryCard/index";
import Container from "./components/cardContainer/index";
import shuffle from 'shuffle-array';
let keyList = [];

class App extends Component {
  constructor(props) {
    super(props)
    this.cardShuffle = this.cardShuffle.bind(this);
    this.cardClick = this.cardClick.bind(this);
  }

  state = {
    images,
    score: 0
  };

  cardShuffle = (state) => {
    let newImages = shuffle(this.state.images);
    this.setState ({
      images: newImages
    })

  }

  getKey = (id) => {
    let myId = this.state.images[id].id
    console.log("MY ID" + myId)
  }



  cardClick = (id) => {
    let key;
    let score = this.state.score;



    if (!keyList.includes(key - 1)) {
      score++;
      this.setState({
        score: score
      });
      key = this.state.images[id].id
      console.log("key pres" + key)
      keyList.push(key - 1);
      console.log("keyList: " + keyList)
      this.getKey(this.state)
      console.log("Object " + JSON.stringify(this.state.images[id -1]));
      this.cardShuffle(this.state);

      console.log("key " + key)

      // console.log("Object " + JSON.stringify(this.state.images[id -1]));

    } else {
      score = 0
      keyList = [];
      this.setState ({
        score: score
      });
      alert("you lost")
    } 
  }

  render() {
    return (
      <Container>
        <Jumbotron />
        <ScoreHeader score={this.state.score} />
        {this.state.images.map(image => (
          <MemoryCard 
          cardClick={this.cardClick} 
          cardShuffle={this.cardShuffle}
          key={image.id} 
          id={image.id}
          name={image.name} 
          image={image.image} 
          />
        ))}
      </Container>
    );
  }
}

export default App;
